E_C("CREATE TABLE `fanwe_user_sta` (
  `user_id` int(11) NOT NULL,
  `dp_count` int(11) NOT NULL COMMENT '留言数',
  `borrow_amount` double(20,4) NOT NULL COMMENT '总的借款数',
  `repay_amount` double(20,4) NOT NULL COMMENT '已还本息',
  `need_repay_amount` double(20,4) NOT NULL COMMENT '待还本息',
  `need_manage_amount` double(20,4) NOT NULL COMMENT '待还管理费',
  `avg_rate` float(10,2) NOT NULL COMMENT '平均借款利率',
  `avg_borrow_amount` double(20,4) NOT NULL COMMENT '平均每笔借款金额',
  `deal_count` int(11) NOT NULL COMMENT '总借入笔数',
  `success_deal_count` int(11) NOT NULL COMMENT '成功借款',
  `repay_deal_count` int(11) NOT NULL COMMENT '还清笔数',
  `tq_repay_deal_count` int(11) NOT NULL COMMENT '提前还清',
  `zc_repay_deal_count` int(11) NOT NULL COMMENT '正常还清',
  `wh_repay_deal_count` int(11) NOT NULL COMMENT '未还清',
  `yuqi_count` int(11) NOT NULL COMMENT '逾期次数',
  `yz_yuqi_count` int(11) NOT NULL COMMENT '严重逾期次数',
  `yuqi_amount` double(20,4) NOT NULL COMMENT '逾期本息',
  `yuqi_impose` double(20,4) NOT NULL COMMENT '逾期费用',
  `load_earnings` double(20,4) NOT NULL COMMENT '已赚利息',
  `load_tq_impose` double(20,4) NOT NULL COMMENT '提前还款违约金',
  `load_yq_impose` double(20,4) NOT NULL COMMENT '逾期还款违约金',
  `load_avg_rate` float(10,2) NOT NULL COMMENT '借出加权平均收益率',
  `load_count` int(11) NOT NULL COMMENT '总借出笔数',
  `load_money` double(20,4) NOT NULL COMMENT '总的借出金额',
  `load_repay_money` double(20,4) NOT NULL COMMENT '已回收本息',
  `load_wait_repay_money` double DEFAULT NULL COMMENT '待回收本息',
  `reback_load_count` int(11) NOT NULL COMMENT '收回的借出笔数',
  `wait_reback_load_count` int(11) NOT NULL COMMENT '未收回的借出笔数',
  PRIMARY KEY (`user_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8");
